sys_ui_page.client_script:
$(function(){
	if(g_user.hasRole("x_manen_assist.admin") || g_user.hasRole("admin") || g_user.hasRole("x_manen_assist.moderator")){
		getSystemProperties();
	}
	else{
		$('#assist_installation').hide();
		$('#assist_restrict_page').show();
	}
});


function showPreloader(){
	document.getElementById("assist_installation").style.opacity=0.5;
	setTimeout(function(){
		updateSystemProperties();
	},1500);
}

function hidePreloader(){
	$("#update_loader").hide();
}

function goBack() {
    window.history.back();
}

function updateSystemProperties(){
	var assist_api_key = document.getElementById('settings_api_key').value;
	var assist_eu_flag = document.getElementById('settings_is_eu').checked;
	var rec = new GlideRecord('sys_properties');
	rec.addQuery('name', 'x_manen_assist.api_key');
	rec.query();
	if(rec.next()){
		rec.value = assist_api_key;
		rec.update();
		var rec1 = new GlideRecord('sys_properties');
		rec1.addQuery('name', 'x_manen_assist.is_eu');
		rec1.query();
		if(rec1.next()){
			rec1.value = assist_eu_flag;
			rec1.update();
			$("#success_msg").show().delay(3000).fadeOut();
			getSystemProperties();
		}
	}
}

function getSystemProperties(){
	var eu_flag=false,api_key=null;
	var rec = new GlideRecord('sys_properties');
	rec.addQuery('name', 'x_manen_assist.api_key');
	rec.query();
	if(rec.next()){
		api_key = rec.value;
		$('#settings_api_key').val(api_key);
	}
	var rec1 = new GlideRecord('sys_properties');
	rec1.addQuery('name', 'x_manen_assist.is_eu');
	rec1.query();
	if(rec1.next()){
		eu_flag = rec1.value;
		$('#settings_is_eu').prop('checked', eu_flag);
	}
	document.getElementById("assist_installation").style.opacity=1.0;
	$("#update_btn").val("Update");
}
